import React, { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = () => {
  // const [enteredTitle, setTitle] = useState("");
  // const [enteredDate, setDate] = useState("");
  // const [enteredAmount, setAmount] = useState("");

  //we can also go for 1 state instead of 3 state by calling useState only once by passing object

  const [userInput, setUserInput] = useState({
    enteredTitle: "",
    enteredAmount: "",
    enteredDate: "",
  });

  const titleChangeHandler = (event) => {
    setTitle(event.target.value)
    // setUserInput({
    //   ...userInput, // we use spread operator so that other data will not get lost
    //   enteredTitle: event.target.value,
    // });
    //// whenever state update depend on previous state we should do like this
    // setUserInput((prevState) => {
    //   return { ...prevState, enteredTitle: event, target, value };
    // });
  };

  const amountChangeHandler = (event) => {
    setAmount(event.target.value);
    setUserInput({
      ...userInput,
      enteredAmount: event.target.value,
    });
  };

  const dateChangeHandler = (event) => {
    setDate(event.target.value);
    setUserInput({
      ...userInput,
      enteredDate: event.target.value,
    });
  };
  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangeHandler} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            type="number"
            min="0.01"
            max="0.01"
            onChange={amountChangeHandler}
          />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            type="Date"
            min="2020-01-01"
            max="2020-12-01"
            onChange={dateChangeHandler}
          />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
